mapper.delete:
DELETE FROM gathering_participant_tb
WHERE gathering_id = #{gatheringId}
AND user_id = #{userId};

mapper.select:
SELECT COUNT(*)
FROM gathering_participant_tb
WHERE gathering_id = #{gatheringId};

SELECT COUNT(*) > 0
FROM gathering_participant_tb
WHERE gathering_id = #{gatheringId} AND user_id = #{userId}

SELECT attendance_status
FROM gathering_participant_tb
WHERE gathering_id = #{gatheringId}
AND user_id = #{userId}

mapper.update:
UPDATE gathering_participant_tb
SET attendance_status = CASE user_id

    WHEN #{dto.userId} THEN #{dto.attendanceStatus}

ELSE attendance_status
END
WHERE gathering_id = #{gatheringId}
AND user_id IN

    #{dto.userId}
